// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract LoanManager {
    struct Loan {
        address borrower;
        uint256 principal;
        uint256 interestRate; // As a percentage
        bool isPaid;
    }

    mapping(address => uint256) public totalDebt; // Total debt per user
    mapping(uint256 => Loan) public loans; // Loan ID to Loan mapping
    uint256 public loanCount;

    event LoanRequested(address indexed borrower, uint256 principal, uint256 interestRate);
    event LoanApproved(uint256 indexed loanId);
    event LoanRepaid(address indexed borrower, uint256 amount);

    function requestLoan(uint256 _principal, uint256 _interestRate) public {
        loans[loanCount] = Loan(msg.sender, _principal, _interestRate, false);
        totalDebt[msg.sender] += _principal; // Update total debt
        emit LoanRequested(msg.sender, _principal, _interestRate);
        loanCount++;
    }

    function approveLoan(uint256 _loanId) public {
        require(loans[_loanId].borrower != address(0), "Invalid loan ID.");
        require(!loans[_loanId].isPaid, "Loan already paid.");
        emit LoanApproved(_loanId);
    }

    function repayLoan(uint256 _loanId) public payable {
        Loan storage loan = loans[_loanId];
        require(msg.sender == loan.borrower, "Only the borrower can repay.");
        require(!loan.isPaid, "Loan already paid.");
        require(msg.value >= loan.principal + (loan.principal * loan.interestRate / 100), "Insufficient funds for repayment.");

        loan.isPaid = true;
        totalDebt[msg.sender] -= loan.principal; // Update total debt
        emit LoanRepaid(msg.sender, loan.principal);
    }
}
